mapper.select:
SELECT div.div_id , div.project_id , div3.name as upup_name , div2.name as up_name , div.name , div.depth , div3.div_id as upupcode , div.upcode , div.reg_user , div.reg_date , div.team_id , coalesce(tm.name, '') as team_name , COALESCE(tm.reader_id, '') , COALESCE(us.name , '') as reader_name , (select count(*) from sftm.itm_div div5 where div.div_id = div5.upcode) as sub_team_cnt FROM sftm.itm_div div LEFT OUTER JOIN sftm.itm_div div2 ON div.upcode = div2.div_id LEFT OUTER JOIN sftm.itm_div div3 ON div2.upcode = div3.div_id LEFt OUTER JOIN sftm.itm_team tm ON div.team_id = tm.id LEFT OUTER JOIN sftm.itm_user us ON tm.reader_id = us.user_id WHERE 1=1
AND div.depth = #{depth}
AND div.upcode = #{upcode}
AND div.upcode in (SELECT div2.div_id FROM sftm.itm_div div2 WHERE div2.depth = 'B' AND div2.upcode = #{upupcode})
order by div3.name , div2.name, div.name

mapper.update:
    	UPDATE sftm.itm_div 
		SET name = #{name}, 
			
				team_id = #{team_id}::bigint,
			
			upcode =  #{upcode}
		WHERE div_id = #{div_id}

mapper.delete:
DELETE
FROM sftm.itm_div div
WHERE div_id = #{div_id}
OR UPCODE = #{div_id}
OR UPCODE IN (SELECT div_id FROM sftm.itm_div indiv WHERE indiv.upcode = #{div_id})

mapper.select:
SELECT sc.scenario_id , sc.scenario_code , sc.scenario_name , sc.div_id , sc.is_batch , sc.description , div.name div_name1 , div2.name div_name2 , div3.name div_name3 , div3.name || ' > ' || div2.name || ' > ' || div.name as div_name_total FROM sftm.itm_scenario sc JOIN sftm.itm_div div ON sc.div_id = div.div_id LEFT OUTER JOIN sftm.itm_div div2 ON div2.div_id = div.upcode LEFT OUTER JOIN sftm.itm_div div3 ON div3.div_id = div2.upcode WHERE 1=1
AND sc.project_id = #{project_id}::bigint
AND sc.div_id = #{depthC}
AND div2.div_id = #{depthB}
AND div3.div_id = #{depthA}
ORDER BY div3.name, div2.name, div.name

SELECT row_number() OVER () as rnum ,COALESCE(div3.name, '') depth1 ,COALESCE(div2.name, '') depth2 ,COALESCE(div.name, '') depth3 ,COALESCE(div3.name, '') || '-' || COALESCE(div2.name, '') || '-' || COALESCE(div.name, '') as div_name ,COALESCE(sftm.GET_CODENAME('C001', cs.state), '') as stateStr ,COALESCE(test.name, '') test_name ,cs.tester_id , cs.dev_id ,COALESCE(dev.name, '') dev_name ,COALESCE(sc.scenario_name, '') scenario_name ,cs.scenario_id ,cs.case_id ,cs.case_name ,cs.imgKey ,v1.defect_cnt ,v1.A001_01 ,v1.A001_02 ,v1.A001_03 ,v1.A001_03 + A001_02 as not_defect_cnt ,v1.B001_01 ,v1.B001_02 ,v1.B001_03 ,v1.B001_04 ,v1.B001_05 ,v1.B001_06 ,v1.B001_07 ,v1.B001_01 + v1.B001_02 + v1.B001_07 as dev_check_cnt ,v1.B001_03 + v1.B001_04 as test_check_cnt ,cs.state ,cs.description ,cs.project_id ,pr.project_name ,sc.scenario_code FROM ( select ics.case_id , ics.scenario_id , COALESCE( count(*) FILTER(WHERE df.defect_type = 'A001_01'), 0) as A001_01 , COALESCE( count(*) FILTER(WHERE df.defect_type = 'A001_02'), 0) as A001_02 , COALESCE( count(*) FILTER(WHERE df.defect_type = 'A001_03'), 0) as A001_03 , COALESCE( count(*) FILTER(WHERE df.defect_code = 'B001_01'), 0) as B001_01 , COALESCE( count(*) FILTER(WHERE df.defect_code = 'B001_02'), 0) as B001_02 , COALESCE( count(*) FILTER(WHERE df.defect_code = 'B001_03'), 0) as B001_03 , COALESCE( count(*) FILTER(WHERE df.defect_code = 'B001_04'), 0) as B001_04 , COALESCE( count(*) FILTER(WHERE df.defect_code = 'B001_05'), 0) as B001_05 , COALESCE( count(*) FILTER(WHERE df.defect_code = 'B001_06'), 0) as B001_06 , COALESCE( count(*) FILTER(WHERE df.defect_code = 'B001_07'), 0) as B001_07 , COALESCE( count(*) FILTER(WHERE df.defect_code != ''), 0) as defect_cnt FROM sftm.itm_test_case ics LEFT OUTER JOIN sftm.itm_defect df ON ics.scenario_id = df.scenario_id and ics.case_id = df.case_id ,sftm.itm_scenario isc where ics.scenario_id = isc.scenario_id
AND isc.project_id = #{project_id}::bigint
AND ics.scenario_id = #{scenario_id}::bigint
AND ics.case_id = #{case_id}::bigint
AND ics.dev_id = #{dev_id}
AND ics.tester_id = #{tester_id}
AND ics.state = #{state}
GROUP BY ics.case_id, ics.scenario_id ) as v1 , sftm.itm_test_case cs LEFT JOIN sftm.itm_user test ON cs.tester_id = test.user_id LEFT JOIN sftm.itm_user dev ON cs.dev_id = dev.user_id LEFT JOIN sftm.itm_scenario sc ON cs.scenario_id = sc.scenario_id , sftm.itm_div div LEFT OUTER JOIN sftm.itm_div div2 ON div2.div_id = div.upcode LEFT OUTER JOIN sftm.itm_div div3 ON div3.div_id = div2.upcode , sftm.itm_project pr where v1.scenario_id = cs.scenario_id and v1.case_id = cs.case_id and cs.project_id = pr.id and sc.div_id = div.div_id
AND div3.div_id = #{divA}
AND div2.div_id = #{divB}
AND div.div_id = #{divC}

  		SELECT DV.DIV_ID 
		FROM SFTM.ITM_DIV AS DV
		WHERE 1=1
		
        	AND DV.NAME = #{name}
        
		
        	AND DV.DEPTH = #{depth}
        
		
        	AND DV.UPCODE = #{upcode}
        
        LIMIT 1

mapper.update:
    	UPDATE sftm.itm_test_case 
		SET state = #{state} 
		WHERE case_code = #{case_code} 
		AND scenario_code 	= #{scenario_code}

mapper.delete:
DELETE
FROM sftm.itm_test_case
WHERE case_id 	= #{case_id} 
AND scenario_id 	= #{scenario_id}

mapper.update:
update sftm.itm_test_case set imgkey = #{imgkey}::bigint
,state = #{state}
,modify_user = #{cookieUserId}
,modify_date = now() WHERE 1=1
and scenario_id = #{scenario_id}::bigint
AND case_id = #{case_id}::bigint
